 */
class Group extends PluginGroup
{
  public function __toString()
  {
    if ( !$this->User || !$this->User->id )
      return parent::__toString();
    else
      return '('.$this->User->username.') '.parent::__toString();
  }
  
  public function getHtmlTag()
  {
    sfApplicationConfiguration::getActive()->loadHelpers(array('CrossAppLink'));
    if ( sfContext::hasInstance() && sfContext::getInstance()->getUser()->getId() )
    {
      $sf_user = sfContext::getInstance()->getUser();
      if (!( $this->sf_guard_user_id == $sf_user->getId() || $this->sf_guard_user_id === null && $sf_user->hasCredential('pr-group-common') ))
        return '';
    }
    
    if ( !$this->display_everywhere )
      return '';
    
    if ( is_object($this->Picture) && !$this->Picture->isNew() )
      return cross_app_link_to($this->Picture->getHtmlTag(array('title' => $this, 'alt' => 'G',)),'rp','group/show?id='.$this->id,false,null,false,'target="_blank" class="picto"');
    else
      return cross_app_link_to('G','rp','group/show?id='.$this->id,false,null,false,'target="_blank" class="picto" title="'.$this.'"');
  }
}
