<?php

/**
 * SurveyAnswerTable
 * 
 * This class has been auto-generated by the Doctrine ORM Framework
 */
class SurveyAnswerTable extends PluginSurveyAnswerTable
{
  public function createQuery($a = 'a')
  {
    return parent::createQuery($a)
      ->leftJoin("$a.Query q")
      ->leftJoin("q.Translation qt")
      ->leftJoin('q.Survey s')
      ->leftJoin("$a.Group g")
      
      ->leftJoin('g.Contact c')
      ->leftJoin('g.Professional p')
      ->leftJoin('p.Organism po')
      ->leftJoin('p.Contact pc')
      
      ->select("$a.*, q.*, qt.*, s.*, g.*")
      ->addSelect('c.*, p.*, po.*, pc.*')
      ->addSelect('s.name survey_name, q.rank query_rank, qt.name query_name');
    ;
  }
    /**
     * Returns an instance of this class.
     *
     * @return object SurveyAnswerTable
     */
    public static function getInstance()
    {
        return Doctrine_Core::getTable('SurveyAnswer');
    }
}
